function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c);a.center()});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var b=$("#verify-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");b.find(".address-result").hide();var a=b.find('textarea[name="message"]');var c=b.find('textarea[name="signature"]');b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});b.find("textarea").bind("change",function(){b.find(".address-result").hide()});b.find(".btn.btn-primary").unbind().click(function(){try{var d=$.trim(a.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(c.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var f=Bitcoin.Message.verifyMessage(h,d);b.find(".address-result-txt").text(f);b.find(".address-result").show(200)}catch(g){makeNotice("error","misc-error","Error Verifying Message"+g);b.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var g=addresses[a];if(!g||!g.priv){b.modal("hide");return}var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var h=decodePK(g.priv);var e=new Bitcoin.ECKey(h);var f=Bitcoin.Message.signMessage(e,d,g.addr);b.find(".signature").show(200);b.find(".signature-result").text(f)})})}function showLabelAddressModal(c){var a=$("#label-address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(c);var b=a.find('input[name="label"]');a.find(".address").text(c);b.val("");a.find(".btn.btn-primary").unbind().click(function(){a.modal("hide");var d=b.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[c].label=d;backupWalletDelayed();buildVisibleView()});a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})};