function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c);a.center()});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var a=$("#verify-message-modal");a.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");a.find(".address-result").hide();var c=a.find('textarea[name="message"]');var b=a.find('textarea[name="signature"]');a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")});a.find("textarea").bind("change",function(){a.find(".address-result").hide()});a.find(".btn.btn-primary").unbind().click(function(){try{var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(b.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var f=Bitcoin.Message.verifyMessage(h,d);a.find(".address-result-txt").text(f);a.find(".address-result").show(200)}catch(g){makeNotice("error","misc-error","Error Verifying Message"+g);a.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var c=$("#sign-message-modal");c.modal({keyboard:true,backdrop:"static",show:true});c.find(".signature").hide();var b=c.find('textarea[name="message"]');c.find(".address").text(a);c.find(".btn.btn-secondary").unbind().click(function(){c.modal("hide")});b.bind("change",function(){c.find(".signature").hide()});c.find(".btn.btn-primary").unbind().click(function(){var g=addresses[a];if(!g||!g.priv){c.modal("hide");return}var d=$.trim(b.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var f=decodePK(g.priv);var e=new Bitcoin.ECKey(f);var h=Bitcoin.Message.signMessage(e,d,g.addr);c.find(".signature").show(200);c.find(".signature-result").text(h)})})}function showLabelAddressModal(b){var a=$("#label-address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);var c=a.find('input[name="label"]');a.find(".address").text(b);c.val("");a.find(".btn.btn-primary").unbind().click(function(){a.modal("hide");var d=c.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[b].label=d;backupWalletDelayed();buildVisibleView()});a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})};