function showAddressModalQRCode(b){var a=$("#qr-code-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(300,300,1,b);a.find(".address-qr-code").empty().append(c);a.center()});a.find(".address").text(b);a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})}function verifyMessageModal(){var a=$("#verify-message-modal");a.modal({keyboard:true,backdrop:"static",show:true});console.log("Verify Message");a.find(".address-result").hide();var c=a.find('textarea[name="message"]');var b=a.find('textarea[name="signature"]');a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")});a.find("textarea").bind("change",function(){a.find(".address-result").hide()});a.find(".btn.btn-primary").unbind().click(function(){try{var f=$.trim(c.val());if(!f||f.length==0){makeNotice("error","misc-error","You Must Enter A Message To Verify");return}var h=$.trim(b.val());if(!h||h.length==0){makeNotice("error","misc-error","You Must Enter A Signature To Verify");return}var d=Bitcoin.Message.verifyMessage(h,f);a.find(".address-result-txt").text(d);a.find(".address-result").show(200)}catch(g){makeNotice("error","misc-error","Error Verifying Message"+g);a.modal("hide");return}})}function showAddressModalSignMessage(a){getSecondPassword(function(){var b=$("#sign-message-modal");b.modal({keyboard:true,backdrop:"static",show:true});b.find(".signature").hide();var c=b.find('textarea[name="message"]');b.find(".address").text(a);b.find(".btn.btn-secondary").unbind().click(function(){b.modal("hide")});c.bind("change",function(){b.find(".signature").hide()});b.find(".btn.btn-primary").unbind().click(function(){var g=addresses[a];if(!g||!g.priv){b.modal("hide");return}var d=$.trim(c.val());if(!d||d.length==0){makeNotice("error","misc-error","You Must Enter A Message To Sign");return}var e=decodePK(g.priv);var f=new Bitcoin.ECKey(e);var h=Bitcoin.Message.signMessage(f,d,g.addr);b.find(".signature").show(200);b.find(".signature-result").text(h)})})}function showLabelAddressModal(b){var a=$("#label-address-modal");a.modal({keyboard:true,backdrop:"static",show:true});a.find(".address").text(b);var c=a.find('input[name="label"]');a.find(".address").text(b);c.val("");a.find(".btn.btn-primary").unbind().click(function(){a.modal("hide");var d=c.val();if(d.length==0){makeNotice("error","misc-error","you must enter a label for the address");return false}addresses[b].label=d;backupWalletDelayed();buildVisibleView()});a.find(".btn.btn-secondary").unbind().click(function(){a.modal("hide")})};