$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(g){var c=$.parseJSON(g.data);if(c.op=="status"){$("#status").html(c.msg)}else{if(c.op=="utx"){op=c.x;try{playSound("beep")}catch(g){console.log(g)}var k=TransactionFromJSON(op);k.setConfirmations(0);var d=0;for(var j=0;j<k.inputs.length;j++){var f=k.inputs[j];console.log(f.prev_out.addr);if(f.prev_out.addr==address){d-=parseInt(f.prev_out.value)}}console.log("result "+d);var h=0;for(var j=0;j<k.out.length;j++){var l=k.out[j];if(l.addr==address){h+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+h);d+=h;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+d);flashTitle("New Transaction");k.result=d;$("#tx_container").prepend(k.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});