$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(j){var c=$.parseJSON(j.data);if(c.op=="status"){$("#status").html(c.msg)}else{if(c.op=="utx"){op=c.x;try{playSound("beep")}catch(j){console.log(j)}var h=TransactionFromJSON(op);h.setConfirmations(0);var k=0;for(var f=0;f<h.inputs.length;f++){var d=h.inputs[f];console.log(d.prev_out.addr);if(d.prev_out.addr==address){k-=parseInt(d.prev_out.value)}}console.log("result "+k);var g=0;for(var f=0;f<h.out.length;f++){var l=h.out[f];if(l.addr==address){g+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+g);k+=g;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);flashTitle("New Transaction");h.result=k;$("#tx_container").prepend(h.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});