$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(d){var c=$.parseJSON(d.data);if(c.op=="status"){$("#status").html(c.msg)}else{if(c.op=="utx"){op=c.x;try{playSound("beep")}catch(d){console.log(d)}var l=TransactionFromJSON(op);l.setConfirmations(0);var j=0;for(var h=0;h<l.inputs.length;h++){var k=l.inputs[h];console.log(k.prev_out.addr);if(k.prev_out.addr==address){j-=parseInt(k.prev_out.value)}}console.log("result "+j);var g=0;for(var h=0;h<l.out.length;h++){var f=l.out[h];if(f.addr==address){g+=parseInt(f.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+g);j+=g;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+j);flashTitle("New Transaction");l.result=j;$("#tx_container").prepend(l.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});