$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var b=new WebSocket(getWebSocketURL());b.onmessage=function(g){var d=$.parseJSON(g.data);if(d.op=="status"){$("#status").html(d.msg)}else{if(d.op=="utx"){op=d.x;try{playSound("beep")}catch(g){console.log(g)}var c=TransactionFromJSON(op);c.setConfirmations(0);var f=0;for(var k=0;k<c.inputs.length;k++){var j=c.inputs[k];console.log(j.prev_out.addr);if(j.prev_out.addr==address){f-=parseInt(j.prev_out.value)}}console.log("result "+f);var h=0;for(var k=0;k<c.out.length;k++){var l=c.out[k];if(l.addr==address){h+=parseInt(l.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+h);f+=h;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+f);flashTitle("New Transaction");c.result=f;$("#no_tx").hide();$("#tx_container").prepend(c.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"addr_sub", "addr":"'+address+'"}')};b.onclose=function(){$("#status").html("Disconnected")}}catch(a){console.log(a)}});