$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(g){var c=$.parseJSON(g.data);if(c.op=="status"){$("#status").html(c.msg)}else{if(c.op=="utx"){op=c.x;try{playSound("beep")}catch(g){console.log(g)}var d=TransactionFromJSON(op);d.setConfirmations(0);var j=0;for(var l=0;l<d.inputs.length;l++){var h=d.inputs[l];console.log(h.prev_out.addr);if(h.prev_out.addr==address){j-=parseInt(h.prev_out.value)}}console.log("result "+j);var f=0;for(var l=0;l<d.out.length;l++){var k=d.out[l];if(k.addr==address){f+=parseInt(k.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+f);j+=f;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+j);flashTitle("New Transaction");d.result=j;$("#no_tx").hide();$("#tx_container").prepend(d.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});