$(document).ready(function(){$("#add-to-wallet").click(function(){goToWallet(address)});$("#deposit").click(function(){loadScript(resource+"wallet/deposit/deposit.js",function(){showDepositModal(address,"bitinstant","Deposit Using Cash")})});$("#payment-request").click(function(){loadScript(resource+"wallet/payment-request.js",function(){showPaymentRequestModal(address,"Payment Request")})});loadScript(resource+"wallet/qr.code.creator.js",function(){var c=makeQRCode(255,250,1,address);$("#qr-code").append(c)});$("#filter").change(function(){$(this).parent().submit()});try{var a=new WebSocket(getWebSocketURL());a.onmessage=function(h){var j=$.parseJSON(h.data);if(j.op=="status"){$("#status").html(j.msg)}else{if(j.op=="utx"){op=j.x;try{playSound("beep")}catch(h){console.log(h)}var l=TransactionFromJSON(op);l.setConfirmations(0);var k=0;for(var d=0;d<l.inputs.length;d++){var g=l.inputs[d];console.log(g.prev_out.addr);if(g.prev_out.addr==address){k-=parseInt(g.prev_out.value)}}console.log("result "+k);var c=0;for(var d=0;d<l.out.length;d++){var f=l.out[d];if(f.addr==address){c+=parseInt(f.value)}}$("#total_received span").attr("data-c",parseInt($("#total_received span").attr("data-c"))+c);k+=c;$("#final_balance span").attr("data-c",parseInt($("#final_balance span").attr("data-c"))+k);flashTitle("New Transaction");l.result=k;$("#tx_container").prepend(l.getHTML());$("#tx-"+op.txIndex).fadeIn("slow").slideDown("slow");console.log($("#n_transactions").val());$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};a.onopen=function(){$("#status").html("Connected. ");a.send('{"op":"addr_sub", "addr":"'+address+'"}')};a.onclose=function(){$("#status").html("Disconnected")}}catch(b){console.log(b)}});