function calculateProfitLoss(c){setLoadingText("Calculating Profit / Loss");var b=c.find('input[name="send-to-address"]');var a=[];$(b).each(function(){a.push($(this).val())});if(a.length==0){return}$.get(root+"walletprofitloss?input_address="+getActiveAddresses().join("|")+"&output_address="+a.join("|")+"&format=plain").success(function(f){var d=c.find(".profit-loss").show(200);d.find(".n-bets").text(f.n_sent+f.n_received);d.find(".n-pending").text(f.n_sent-f.n_received);var e=f.total_received-f.total_sent;if(e>0){d.find(".winnings").html('<font color="green">'+formatMoney(e,true)+"</font>")}else{if(e<0){d.find(".winnings").html('<font color="red">'+formatMoney(e,true)+"</font>")}else{d.find(".winnings").html(formatMoney(e,true))}}d.find(".refresh").unbind().click(function(){calculateProfitLoss(c)})})}function buildForm(b){var a=b.find(".recipient-container");if(!a.is(":empty")){return}$.get(root+"dicegames?game="+b.data("name")).success(function(j){a.empty();var f=$('<div class="control-group"><label class="control-label">Win Odds</label><div class="controls"><p>Enter the amounts you wish to bet below:</p></div></div>');a.append(f);for(var h in j){var c=j[h];if(c.popular){a.append('<div class="control-group recipient"><label class="control-label">'+c.odds+'%</label><div class="controls"><input name="send-to-address" type="hidden" value="'+c.address+'" /><div class="input-append"> <input class="send-value" style="width:auto;max-width:145px;" data-optional="true" name="send-value" data-multiplier="'+c.multiplier+'" data-minbet="'+c.minBet+'" data-maxbet="'+c.maxBet+'" placeholder="Bet Amount (BTC)" type="text" /><span class="add-on send-win-amount">No Bet</span> </div></div></div>')}}function g(e){if(e.val()>0){e.parent().find(".send-win-amount").html("Win Amount: "+(parseFloat(e.val())*parseFloat(e.data("multiplier"))).toFixed(4)+" BTC")}else{e.parent().find(".send-win-amount").html("No Bet")}}a.find('input[name="send-value"]').change(function(){if($(this).val()>$(this).data("maxbet")){$(this).val($(this).data("maxbet"));makeNotice("error","misc-error","The Maximum Bet is "+$(this).data("maxbet")+" BTC")}if($(this).val()==0){$(this).val("")}else{if($(this).val()<$(this).data("minbet")){$(this).val($(this).data("minbet"));makeNotice("error","misc-error","The Minimum Bet is "+$(this).data("minbet")+" BTC")}}g($(this))});a.find('input[name="send-value"]').keyup(function(){g($(this))});b.find(".send").unbind().click(function(){loadScript(resource+"wallet/signer.min.js",function(){startTxUI(b,"dice",initNewTx())})});try{calculateProfitLoss(b)}catch(d){console.log(d)}}).error(function(){makeNotice("error","misc-error","Error Downloading "+b.data("name")+" Bets")})};