function calculateProfitLoss(c){setLoadingText("Calculating Profit / Loss");var b=c.find('input[name="send-to-address"]');var a=[];$(b).each(function(){a.push($(this).val())});if(a.length==0){return}$.get(root+"walletprofitloss?input_address="+getActiveAddresses().join("|")+"&output_address="+a.join("|")+"&format=plain").success(function(d){var f=c.find(".profit-loss").show(200);f.find(".n-bets").text(d.n_sent+d.n_received);f.find(".n-pending").text(d.n_sent-d.n_received);var e=d.total_received-d.total_sent;if(e>0){f.find(".winnings").html('<font color="green">'+formatMoney(e,true)+"</font>")}else{if(e<0){f.find(".winnings").html('<font color="red">'+formatMoney(e,true)+"</font>")}else{f.find(".winnings").html(formatMoney(e,true))}}f.find(".refresh").unbind().click(function(){calculateProfitLoss(c)})})}function buildForm(a){var b=a.find(".recipient-container");if(!b.is(":empty")){return}$.get(root+"dicegames?game="+a.data("name")).success(function(f){b.empty();var d=$('<div class="control-group"><label class="control-label">Win Odds</label><div class="controls"><p>Enter the amounts you wish to bet below:</p></div></div>');b.append(d);for(var g in f){var c=f[g];if(c.popular){b.append('<div class="control-group recipient"><label class="control-label">'+c.odds+'%</label><div class="controls"><input name="send-to-address" type="hidden" value="'+c.address+'" /><div class="input-append"> <input class="send-value" style="width:auto;max-width:145px;" data-optional="true" name="send-value" data-multiplier="'+c.multiplier+'" data-minbet="'+c.minBet+'" data-maxbet="'+c.maxBet+'" placeholder="Bet Amount (BTC)" type="text" /><span class="add-on send-win-amount">No Bet</span> </div></div></div>')}}function j(e){if(e.val()>0){e.parent().find(".send-win-amount").html("Win Amount: "+(parseFloat(e.val())*parseFloat(e.data("multiplier"))).toFixed(4)+" BTC")}else{e.parent().find(".send-win-amount").html("No Bet")}}b.find('input[name="send-value"]').change(function(){if($(this).val()>$(this).data("maxbet")){$(this).val($(this).data("maxbet"));makeNotice("error","misc-error","The Maximum Bet is "+$(this).data("maxbet")+" BTC")}if($(this).val()==0){$(this).val("")}else{if($(this).val()<$(this).data("minbet")){$(this).val($(this).data("minbet"));makeNotice("error","misc-error","The Minimum Bet is "+$(this).data("minbet")+" BTC")}}j($(this))});b.find('input[name="send-value"]').keyup(function(){j($(this))});a.find(".send").unbind().click(function(){loadScript(resource+"wallet/signer.min.js",function(){startTxUI(a,"dice",initNewTx())})});try{calculateProfitLoss(a)}catch(h){console.log(h)}}).error(function(){makeNotice("error","misc-error","Error Downloading "+a.data("name")+" Bets")})};